var slideIndex = 0;
var slideTimer = null;

document.addEventListener("DOMContentLoaded", function() {
    glossaryNews.slice(0, 3).forEach(announcement => {
        const announcementElement = createAnnouncementElement(announcement);
        announcementContainer.appendChild(announcementElement);
    });

    var slides = document.getElementsByClassName("welcome");

    function showSlides(slides) {
        for (var i = 0; i < slides.length; i++) {
slides[i].classList.remove("active");
        }
        slideIndex++;
        if (slideIndex > slides.length) {
slideIndex = 1;
        }
        slides[slideIndex - 1].classList.add("active");
        slideTimer = setTimeout(() => showSlides(slides), 5000); // 更改这里的数字可以更改轮播间隔时间
    }

    for (var i = 0; i < slides.length; i++) {
        var link = slides[i].querySelector("a");
        link.addEventListener("click", function (e) {
e.stopPropagation();
        });
    }

    showSlides(slides);
});





function plusSlides(n) {
	clearTimeout(slideTimer);
	slideIndex += n;
	if (slideIndex > slides.length) {
slideIndex = 1;
	} else if (slideIndex < 1) {
slideIndex = slides.length;
	}
	for (var i = 0; i < slides.length; i++) {
slides[i].classList.remove("active");
	}
	slides[slideIndex - 1].classList.add("active");
	slideTimer = setTimeout(showSlides, 10000);
}




