// 创建一个函数来动态地生成 HTML 内容
function generateRecommendationCard(recommendation) {
    const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);
    const isSmallScreen = window.innerWidth < 768;
    const tooltipAttr = isMobile && isSmallScreen ? '' : `data-toggle=tooltip data-placement="bottom" title="${recommendation.tooltip}"`;

    const visibleLabels = recommendation.labels.slice(0, 2);
    const hiddenLabel = recommendation.labels[2];

    // Check if the recommendation has "今日推荐" or "热门" label
    const isNew = recommendation.labels.includes("所有工具");
    const isHot = recommendation.labels.includes("热门");

    const card = `
<div class="col-sm-3" data-tooltip="${recommendation.tooltip}">
    <div class="recommendation-card" onclick="window.open('${recommendation.link}', '_blank')" ${tooltipAttr}>
        <div class="card-image-container">
<img src="${recommendation.image}" class="card-image" width="200" height="160" alt="${recommendation.title}">
${isNew ? '<span class="new-tag">NEW</span>' : ''}
${isHot ? '<span class="hot-tag">HOT</span>' : ''}
        </div>
        <div class="card-content">
<div class="card-name">${recommendation.title}</div>
<div class="card-description">${recommendation.description}</div>
<div class="card-tags">
    ${visibleLabels.map(label => `<span class="card-tag">${label}</span>`).join("")}
    <span class="card-tag" style="display:none;">${hiddenLabel}</span>
</div>
        </div>
    </div>
</div>
        `;
    return card;

}


const itemsPerPage = 12;
        let filteredRecommendations = recommendations;
        let currentPage = 1;
const visiblePages = 5;

        function renderCards() {
const start = (currentPage - 1) * itemsPerPage;
const end = start + itemsPerPage;
const cards = filteredRecommendations.slice(start, end).map(generateRecommendationCard).join("");
$("#recommendations-container").html(cards);
	

        }

        function generatePagination() {
const totalPages = Math.ceil(filteredRecommendations.length / itemsPerPage);
let paginationItems = '';
        
// 更新页面信息
$("#page-info").text(`共 ${filteredRecommendations.length} 条`);
$("#page-input").val(`第 ${currentPage}/${totalPages} 页`);
        
// Previous button
paginationItems += `<li class="page-item ${currentPage === 1 ? 'disabled' : ''}"><a class="page-link" href="#" aria-label="Previous" style="width:60px;text-align:center;"><span aria-hidden="true">&laquo;</span></a></li>`;
        
const firstVisible = Math.max(1, currentPage - Math.floor(visiblePages / 2));
const lastVisible = Math.min(totalPages, firstVisible + visiblePages - 1);
        
for (let i = firstVisible; i <= lastVisible; i++) {
    paginationItems += `<li class="page-item ${i === currentPage ? 'active' : ''}"><a class="page-link" href="#">${i}</a></li>`;
}
        
// Next button
paginationItems += `<li class="page-item ${currentPage === totalPages ? 'disabled' : ''}"><a class="page-link" href="#" aria-label="Next" style="width:60px;text-align:center;"><span aria-hidden="true">&raquo;</span></a></li>`;
        
$("#pagination").html(paginationItems);
        }


        function filterCards(keyword = '', category = '所有工具') {
currentPage = 1;
filteredRecommendations = recommendations.filter(recommendation => {
    const matchesKeyword = keyword === '' || (recommendation.title + recommendation.description + recommendation.labels.join("")).toLowerCase().includes(keyword.toLowerCase());
    const matchesCategory = category === '所有工具' || recommendation.labels.includes(category);
    return matchesKeyword && matchesCategory;
});
           renderCards();
           renderPagination();  // 添加了对 renderPagination 的调用
           generatePagination();  // 添加了对 generatePagination 的调用
        }

